mapper.select:
select bp.*,
	,
	,

from
    biz_room r JOIN biz_puchs bp  ON r.room_code=bp.room_code
	JOIN biz_hotl h ON r.hotel_code =h.hotel_code
 	JOIN biz_room_ext d ON  r.room_code=d.room_code


			AND bp.order_code = #{orderCode}


			AND bp.status = #{roomStatus}


			AND bp.create_time <=  DATE_FORMAT(#{createTimeEnd}, '%Y-%m-%d 24:%i:%S')


			AND bp.create_time >=  DATE_FORMAT(#{createTimeStart}, '%Y-%m-%d %H:%i:%S')


			AND bp.last_crt_time <= #{confirmTimeEnd}


			AND bp.last_crt_time >= #{confirmTimeStart}


			AND (h.hotel_cname = #{hotelName} OR h.hotel_ename = #{hotelName})


			AND bp.create_name = #{createName}

mapper.update:
update  biz_puchs


		status = #{status,jdbcType=VARCHAR},


where status = #{oldStatus,jdbcType=VARCHAR} AND  in_date_start <= #{inDateStart,jdbcType=VARCHAR}
