mapper.select:
SELECT
    ,
    ,
    ,
  (select para_code from sys_para_config where para_code LIKE 'roomtype%' and para_sub_code1='bizroom' AND para_value1=a.room_type) room_type_key,
  (select para_code from sys_para_config where para_code LIKE 'bedtype%' and para_sub_code1='bizroom' AND para_value1=a.bed_type)  bed_type_key,
  (select para_code from sys_para_config where para_code LIKE 'breakType%' and para_sub_code1='bizroom' AND para_value1=a.break_type)  break_type_key,
  (select para_code from sys_para_config where para_code LIKE 'province%' and para_sub_code1='provinceCode' AND para_value1=b.province_code)  province_code_key,
  (select para_code from sys_para_config where para_code LIKE 'cityCode%' and para_sub_code1='cityCode' AND para_value1=b.city_code)  city_code_key,
  (select para_code from sys_para_config where para_code LIKE 'hotelType%' and para_sub_code1='hotelType' AND para_value1=b.hotel_type)  hotel_type_key,
    ,

FROM
    biz_room a
    JOIN biz_hotl b ON a.hotel_code = b.hotel_code
    JOIN biz_room_ext bre ON a.room_code = bre.room_code
      JOIN (
          (SELECT
              f.room_code,
              avg(g.s_price) price,
              min(bi.inventory) inventory
          FROM
              (SELECT
                    x.date,
                    c.room_code
              FROM
                    biz_room c
              JOIN ( SELECT DATE_FORMAT( date, '%Y%m%d' ) date
                     FROM calendar
                     WHERE date BETWEEN DATE_FORMAT( #{inDateStart}, '%Y%m%d') AND DATE_FORMAT( #{outDateEnd}, '%Y%m%d')
                     ) x
              ) f
              LEFT JOIN biz_inv bi ON f.room_code = bi.room_code AND f.date = bi.inv_date
              LEFT JOIN biz_prise g ON f.room_code = g.room_code and f.date = g.price_date
          WHERE
              g.s_price is not null
          GROUP BY
              f.room_code
          HAVING
          count( f.room_code ) = DATEDIFF(DATE_FORMAT( #{outDateEnd}, '%Y%m%d'),DATE_FORMAT( #{inDateStart}, '%Y%m%d') ) +1)
          ) p ON a.room_code = p.room_code
WHERE
    a.del_flag = 0
    AND a.room_code IN (
      SELECT
          brd.room_code
      FROM
      (
          SELECT
              d.date,
              br.room_code,
              br.room_type,
              br.room_style,
              br.bed_type,
              br.break_type,
              br.room_area,
              br.room_stock brs
          FROM
              biz_room br
          JOIN ( SELECT
                    DATE_FORMAT( date, '%Y%m%d' ) date
                 FROM calendar
                 WHERE date BETWEEN DATE_FORMAT( #{inDateStart}, '%Y%m%d') AND DATE_FORMAT( #{outDateEnd}, '%Y%m%d') ) d
      ) brd
      LEFT JOIN biz_inv bi ON brd.room_code = bi.room_code AND brd.date = bi.inv_date
      LEFT JOIN biz_prise bp ON brd.room_code = bp.room_code AND brd.date = bp.price_date
      WHERE


                  IFNULL( bi.inventory, brd.brs ) >= #{roomNum}


                  IFNULL( bi.inventory, brd.brs ) > 0


          AND bp.s_price IS NOT NULL
          GROUP BY
          brd.room_code
      HAVING
          count( brd.room_code ) = DATEDIFF(DATE_FORMAT( #{outDateEnd}, '%Y%m%d'),DATE_FORMAT( #{inDateStart}, '%Y%m%d') ) +1
      )

  AND b.province_code = #{provinceCode,jdbcType=VARCHAR}


  AND b.city_code = #{cityCode,jdbcType=VARCHAR}


  AND b.hotel_type = #{hotelType,jdbcType=VARCHAR}


  AND b.hotel_level = #{hotelLevel,jdbcType=VARCHAR}


  AND (b.hotem_cname LIKE CONCAT('%',#{hotelName},'%') or b.hotel_ename LIKE CONCAT('%',#{hotelName},'%'))


  AND a.room_type = #{roomType,jdbcType=VARCHAR}


  AND a.bed_type = #{bedType,jdbcType=VARCHAR}


  AND a.break_type = #{breakType,jdbcType=VARCHAR}


  AND a.room_style = #{roomStyle,jdbcType=VARCHAR}


  AND a.room_area >= #{roomArea,jdbcType=VARCHAR}


  AND bre.iswify = #{iswify,jdbcType=VARCHAR}


  AND bre. isfront = #{isfront,jdbcType=VARCHAR}


  AND bre.isbarrifr = #{isbarrifr,jdbcType=VARCHAR}


  AND bre.isbalcony = #{isbalcony,jdbcType=VARCHAR}


    AND bre.iskitchen = #{iskitchen,jdbcType=VARCHAR}


    AND bre.iswindow = #{iswindow,jdbcType=VARCHAR}


    AND bre.isheat = #{isheat,jdbcType=VARCHAR}


    AND bre.isicebox = #{isicebox,jdbcType=VARCHAR}


    AND bre.iswindow = #{iswindow,jdbcType=VARCHAR}


    AND bre.isheat = #{isheat,jdbcType=VARCHAR}


    AND bre.isicebox = #{isicebox,jdbcType=VARCHAR}


    AND bre.isiron = #{isiron,jdbcType=VARCHAR}


    AND bre.isnosmk = #{isnosmk,jdbcType=VARCHAR}


    AND bre.islandscape = #{islandscape,jdbcType=VARCHAR}


    AND bre.ishighrise = #{ishighrise,jdbcType=VARCHAR}


    AND bre.ispark = #{ispark,jdbcType=VARCHAR}


    AND bre.isgym = #{isgym,jdbcType=VARCHAR}


    AND bre.isswmp = #{isswmp,jdbcType=VARCHAR}


    AND bre.isbeach = #{isbeach,jdbcType=VARCHAR}


    AND bre.ishotsp = #{ishotsp,jdbcType=VARCHAR}


    AND bre.ischildct = #{ischildct,jdbcType=VARCHAR}


    AND bre.isroomserv = #{isroomserv,jdbcType=VARCHAR}


    AND bre. isknead = #{ isknead,jdbcType=VARCHAR}


    AND bre.islounge = #{islounge,jdbcType=VARCHAR}


    AND bre.issuper = #{issuper,jdbcType=VARCHAR}


    AND bre.isbus = #{isbus,jdbcType=VARCHAR}


    AND bre.istrafic = #{istrafic,jdbcType=VARCHAR}


    AND bre.isrestau = #{isrestau,jdbcType=VARCHAR}




            AND p.price <= #{lowRoomPrice}


            AND p.price <= #{highRoomPrice}


            AND p.price BETWEEN #{lowRoomPrice} AND #{highRoomPrice}
