select a.hotel_code hotel_code, d.hotel_cname hotel_cname, d.hotel_ename hotel_ename, d.province_code province_code, d.city_code city_code, (select para_code from sys_para_config where para_code LIKE 'roomtype%' and para_sub_code1='bizroom' AND para_value1=a.room_type) room_type, (select para_code from sys_para_config where para_code LIKE 'bedtype%' and para_sub_code1='bizroom' AND para_value1=a.bed_type) bed_type, b.inventory inventory, c.s_room_price s_room_price from biz_room a LEFT JOIN biz_inv b ON a.room_code=b.room_code LEFT JOIN biz_prise c ON a.room_code=c.room_code and b.inv_date=c.price_date LEFT JOIN biz_hotl d ON a.hotel_code = d.hotel_code WHERE 1=1
AND d.hotel_code = #{hotelCode,jdbcType=VARCHAR}
AND d.hotel_cname LIKE '%'#{hotelName,jdbcType=VARCHAR}'%' OR d.hotel_ename LIKE '%'#{hotelName,jdbcType=VARCHAR}'%'
AND a.room_type = #{roomType,jdbcType=VARCHAR}
AND a.bed_type = #{bedType,jdbcType=VARCHAR}
AND a.break_type = #{breakType,jdbcType=VARCHAR}
AND b.inventory = #{inventory,jdbcType=VARCHAR} AND c.inv_date = to_char(sysdate,'yyyyMMdd')

select
a.room_code     room_code,
a.hotel_code    hotel_code,
d.hotel_cname   hotel_cname,
d.hotel_ename   hotel_ename,
(select para_code from sys_para_config where para_code LIKE 'hotelType%' and para_sub_code1='hotelType' AND para_value1=d.hotel_type) hotel_type_key,
