a.room_style   room_style,
(select para_code from sys_para_config where para_code LIKE 'breaktype%' and para_sub_code1='bizroom' AND para_value1=a.break_type)  break_type_key,
a.room_area    room_area,
a.intro_c      intro_c,
a.intro_e      intro_e,
a.room_stock   room_stock,
a.recommended  recommended,
e.iswify       iswify     ,
e.isfront      isfront    ,
e.isbarrifr    isbarrifr  ,
e.isbalcony    isbalcony  ,
e.iskitchen    iskitchen  ,
