e.istrafic     istrafic   ,
e.isrestau     isrestau
from biz_room a , biz_inv b,biz_prise c,biz_hotl d ,biz_room_ext e   where 1=1 and a.room_code=b.room_code and   a.room_code=c.room_code  and
a.hotel_code = d.hotel_code and  a.room_code = e.room_code

  AND d.province_code = #{provinceCode,jdbcType=VARCHAR}


  AND d.hotel_type = #{hotelType,jdbcType=VARCHAR}


  AND d.hotel_level = #{hotelLevel,jdbcType=VARCHAR}


  AND d.city_code = #{cityCode,jdbcType=VARCHAR}


  AND d.hotel_cname LIKE concat('%',#{hotelName,jdbcType=VARCHAR},'%') OR d.hotel_ename LIKE concat('%',#{hotelName,jdbcType=VARCHAR},'%')


  AND a.room_type = #{roomType,jdbcType=VARCHAR}


  AND a.bed_type = #{bedType,jdbcType=VARCHAR}


  AND a.break_type = #{breakType,jdbcType=VARCHAR}


  AND b.inventory = #{inventory,jdbcType=VARCHAR} AND c.inv_date = to_char(sysdate,'yyyyMMdd')


  AND a.room_code = #{roomCode,jdbcType=VARCHAR}


  AND a.room_style, = #{roomStyle,,jdbcType=VARCHAR}


  AND a.room_area >= #{roomArea,jdbcType=VARCHAR}


  AND a.room_stock = #{roomStock,jdbcType=VARCHAR}


  AND e.iswify = #{iswify,jdbcType=VARCHAR}


